const siteWrapper=document.querySelector("#site-wrapper"),warningPage=document.querySelector(".warning"),hamburger=document.querySelector(".hamburger"),menu=document.querySelector(".menu"),languages=document.querySelector(".languages"),body=document.querySelector("body"),loaderWrapper=document.querySelector("#loader-wrapper"),themeToggler=document.querySelector("#theme__toggle"),warningResolution=window.matchMedia("(max-width: 319px)");document.onreadystatechange=(()=>{if(document.readyState!=="complete"){body.style.visibility="hidden";loaderWrapper.style.visibility="visible"}else{loaderWrapper.style.display="none";loaderWrapper.style.visibility="hidden";body.style.visibility="visible";siteWrapper.style.opacity="1";setThemeStart()}});function setTheme(e){document.documentElement.className=e;localStorage.setItem("theme",e)}function setThemeStart(){if(localStorage.getItem("theme")==="theme-dark"){setTheme("theme-dark");themeToggler.checked=true}else{setTheme("theme-light");themeToggler.checked=false}}themeToggler.addEventListener("change",()=>{if(localStorage.getItem("theme")==="theme-light"){setTheme("theme-dark");themeToggler.checked=true}else{setTheme("theme-light");themeToggler.checked=false}});if(warningResolution.matches){siteWrapper.style.display="none";warningPage.style.display="flex"}hamburger.addEventListener("click",()=>{menu.classList.toggle("menu_active");hamburger.classList.toggle("hamburger_active");languages.classList.toggle("languages_active");body.classList.toggle("lock-scroll")});document.querySelectorAll(".menu__link, .menu__overlay").forEach(e=>{e.addEventListener("click",()=>{menu.classList.remove("menu_active");hamburger.classList.remove("hamburger_active");languages.classList.remove("languages_active");body.classList.remove("lock-scroll")})});